def check_3(aa):
    aa = str(aa).strip().upper()
    if aa in aa_dict:
        return True
    return False

def check_1(aa):
    aa = str(aa).strip().upper()
    for a in aa_dict:
        if aa_dict[a] == aa:
            return True
    return False


def get_1(three):
    three = str(three).strip().upper()
    if check_1(three):
        return three
    elif three in aa_dict:
        return aa_dict[three]
    else:
        return None


def get_3(one):
    one = str(one).strip().upper()
    if check_3(one):
        return one
    for aa in aa_dict:
        if one == aa_dict[aa]:
            return aa
    return None


def get_dict():
    return aa_dict


